import"./main.css";const API_KEY="L89HBC8Z8F345QL3ZZ7X74ZGU",weatherDisplay=document.getElementById("weatherDisplay"),submitBtn=document.getElementById("weatherSubmit");submitBtn.addEventListener("click",async()=>{const e=document.getElementById("location").value;if(!e)return alert("Please enter location!");const n=`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${e}?unitGroup=metric&key=${API_KEY}&contentType=json`;try{const e=await fetch(n),t=await e.json(),s=t.currentConditions,r=s.sunrise,o=s.sunset;weatherDisplay.innerHTML=`\n            <h2>Weather in ${t.resolvedAddress}</h2>\n            <p><strong>Temperature: </strong>${s.temp}째C</p>\n            <p><strong>Humidity:</strong> ${s.humidity}%</p>\n            <p><strong>Wind Speed:</strong> ${s.windspeed} km/h</p>\n            <p><strong>Sunrise:</strong> ${r}</p>\n            <p><strong>Sunset:</strong> ${o}</p>\n            <p><strong>Min Temp:</strong> ${t.days[0].tempmin} 째C</p>\n            <p><strong>Max Temp:</strong> ${t.days[0].tempmax} 째C</p>\n            <p><strong>Real Feel:</strong> ${s.feelslike} 째C</p>\n        `,console.log("raw: ",s.sunrise),console.log("raw set: ",s.sunset)}catch(n){weatherDisplay.innerHTML=`<p>Error fetching weather data in ${e}. please double check the spelling and try again later.</p>`,console.error(n)}});